<?php class Common extends CI_Controller {	public function __construct() {		parent::__construct();		$this->load->model("admin/common_model");		 $this->load->library("pagination");	}	public function change_status()	{		$table = $this->input->post('table');				$column = $this->input->post('column');		$value = $this->input->post('value');		$uniqueNameCol = $this->input->post('uniqueNameCol');		$uniqueColValue = $this->input->post('uniqueColValue');			$re = $this->common_model->change_status($table, $column, $value, $uniqueNameCol, $uniqueColValue);			}}