<?php class Common_model extends CI_Model {	public function insert_data($data,$tbl_name)	{			$sql = $this->db->insert($tbl_name,$data);		return ( $this->db->_error_number() === 0 );	}	public function get_all_list($tbl_name,$limit = 0, $limit_start = 0,$orderby=0,$col=0)	{			$sql = "SELECT * from $tbl_name";		if($orderby)		{			$sql .= " ORDER BY  $col $orderby";		}		if ( $limit > 0 ) {			$sql .= " LIMIT $limit_start, $limit";		}		$query = $this->db->query($sql);		return $query->result_array();	}	public function change_status($table, $column, $value, $uniqueNameCol, $uniqueColValue)	{		$query = $this->db->query("UPDATE ".$table." SET `".$column."` = '".$value."' WHERE `".$uniqueNameCol."` = '".$uniqueColValue."' ");			return true;	}	public function get_item_by_id($tbl_name, $col,$value)	{			$this->db->where($col,$value);		$query = $this->db->get($tbl_name);			if($query->num_rows>1)		{			return $query->result_array();		}		else{		return $query->row_array();		}	}	public function update_item_by_id($tbl_name, $data_arr, $cnd_arr) 	{			$this->db->update($tbl_name, $data_arr, $cnd_arr);		return ( $this->db->_error_number() === 0 );	}	public function delete_by_id($tbl_name,$col,$val){		$query = $this->db->query("DELETE FROM ".$tbl_name." WHERE $col = $val ");			return true;	}	public function restuarant_settings(){		$qry = $this->db->query("Select * from tbl_restuarant_settings");		return $qry->row_array();	}	public function update_res_setting($id,$data){		$qry = $this->db->where('id',$id);		$qry = $this->db->update('tbl_restuarant_settings',$data);		return (boolean) $qry;	}}